const processReviewData = (data) => {
  const reviewCounts = {};

  data.forEach(entry => {
    const { date_of_stay, Sentiment } = entry;

    // Initialize the date in the object if not present
    if (!reviewCounts[date_of_stay]) {
      reviewCounts[date_of_stay] = { Positive: 0, Negative: 0 };
    }

    // Count Positive and Negative sentiments
    reviewCounts[date_of_stay][Sentiment] += 1;
  });

  return reviewCounts;
};

const reviewCounts = processReviewData(rawData);
