// Helper function to parse date strings
const parseDateString = (dateString) => {
  // Assuming the format is DD/MM/YYYY
  const [day, month, year] = dateString.split('/');
  return new Date(`${day}-${month}-${year}`); // Convert to standard YYYY-MM-DD for sorting
};

const prepareChartData = (reviewCounts) => {
  const positiveData = [];
  const negativeData = [];

  // Sort the dates (keys of reviewCounts) in ascending order (oldest to newest)
  const sortedDates = Object.keys(reviewCounts).sort((a, b) => {
    return parseDateString(a) - parseDateString(b);
  });

  // Convert reviewCounts object into chart format based on sorted dates
  sortedDates.forEach(date => {
    positiveData.push({ x: date, y: reviewCounts[date].Positive });
    negativeData.push({ x: date, y: reviewCounts[date].Negative });
  });

  return [
    {
      id: "Compliments",
      color: "hsl(120, 70%, 50%)",
      data: positiveData,
    },
    {
      id: "Complaints",
      color: "hsl(0, 70%, 50%)",
      data: negativeData,
    },
  ];
};

// Final Chart Data
export const chartData = prepareChartData(reviewCounts);


// Function to process rawData into trip_type counts
const processTripTypeData = (data) => {
    const tripTypeCounts = {};

    data.forEach(entry => {
        const { trip_type } = entry;

        if (tripTypeCounts[trip_type]) {
            tripTypeCounts[trip_type] += 1;
        } else {
            tripTypeCounts[trip_type] = 1;
        }
    });

    // Transform into array for pie chart format
    return Object.keys(tripTypeCounts).map(type => ({
        id: type,
        label: type,
        value: tripTypeCounts[type],
    }));
};

// Exporting the pie chart data
export const pieData = processTripTypeData(rawData);

export const extractedData = rawData.map(({ Review_ID, review }) => ({
    Review_ID,
    review: review.length > 100 ? `${review.slice(0, 50)}...` : review // Limit the review length
}));
